// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [newQuiz, setNewQuiz] = useState({ question: '', options: ['', '', '', ''], answer: '' });
  const [error, setError] = useState('');
  const [sets, setSets] = useState([]);
  const [showSets, setShowSets] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/api/quizzes')
      .then(response => setQuizzes(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleChange = (e) => {
    setNewQuiz({ ...newQuiz, [e.target.name]: e.target.value });
  };

  const handleOptionChange = (index, value) => {
    const options = [...newQuiz.options];
    options[index] = value;
    setNewQuiz({ ...newQuiz, options });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newQuiz.options.includes(newQuiz.answer)) {
      setError('The answer must be one of the options');
      return;
    }
    axios.post('http://localhost:5000/api/quizzes', newQuiz)
      .then(response => {
        setQuizzes([...quizzes, response.data]);
        setNewQuiz({ question: '', options: ['', '', '', ''], answer: '' });
        setError('');
      })
      .catch(error => {
        console.error(error);
        setError('Failed to add quiz');
      });
  };

  const handleSetCreation = () => {
    axios.post('http://localhost:5000/api/sets')
      .then(response => {
        setSets([...sets, response.data]);
      })
      .catch(error => {
        console.error(error);
        setError('Failed to create set');
      });
  };

  const handleSetToggle = () => {
    setShowSets(!showSets);
  };

  return (
    <div className="container">
      <h1>Quiz App</h1>
      <div>
        <button onClick={handleSetCreation}>Make a Set</button>
        <button onClick={handleSetToggle}>Toggle Sets</button>
      </div>
      <form onSubmit={handleSubmit}>
        {/* Form inputs for quiz */}
      </form>
      {error && <p className="error">{error}</p>}
      {/* Quiz display */}
      {quizzes.map(quiz => (
        {/* Quiz component */}
      ))}
      {/* Set display */}
      {showSets && (
        <div>
          <h2>Sets</h2>
          {sets.map(set => (
            <div key={set._id}>
              <h3>{set.name}</h3>
              {/* Render quizzes associated with this set */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
